Events.OnRefuel:
				; Chance that this will enhance the ship's drive
				(if (leq (random 1 100) (multiply 2 (itmGetCount gItem)))
					(objEnumItems gSource "rI" theItem
						(switch
							(itmIsDamaged theItem)
								(block Nil
									(objSendMessage gSource Nil (cat "Your " (itmGetName theItem 0) " has been repaired."))
									(shpRepairItem gSource theItem)
									)

							(not (itmIsEnhanced theItem))
								(block Nil
									(objSendMessage gSource Nil (cat "Your " (itmGetName theItem 0) " has been enhanced."))
									(objEnhanceItem gSource theItem {
										enhancement: "+bonus"
										type: &itHeliotropeFuel;
										})
									)
							)
						)
					)

Events.OnObjDestroyed:
(if (eq gSource aObjDestroyed)

	; If our object gets destroyed and we don't end up on the
	; resulting wreck, then we create our own object.
	
	(if (or (not aWreckObj) (not (objGetItems aWreckObj "* +unid:&itStargateControlRod;")))
		(block (theObj)
			(setq theObj (sysCreateFlotsam &itStargateControlRod; (objGetPos gSource) &svNeutral;))
			(objIncVel theObj (sysVectorPolarVelocity (random 0 359) 5))
			)
		)
	)

Events.OnRefuel:
				; Find or create an overlay that will generate radioactive waste
				(if (eq gSource gPlayerShip)
					(block (theID)
						(enum (objGetOverlays gSource) theOverlay
							(if (eq (objGetOverlayType gSource theOverlay) &ovUraniumWasteCheck;)
								(setq theID theOverlay)
								)
							)
							
						(if theID
							(objIncOverlayData gSource theID "wasteCount" (itmGetCount gItem))

							(block Nil
								(setq theID (objAddOverlay gSource &ovUraniumWasteCheck;))
								(objSetOverlayData gSource theID "wasteCount" (itmGetCount gItem))
								(objSetOverlayData gSource theID "counter" 0)
								)
							)
						)
					)

Events.OnUpdate:
				(block (
					(deathOn (itm@ gItem 'deathOn))
					(now (unvGetTick))
					(timeToDieInSeconds (max 0 (round (/ (- deathOn now) 30))))
					(lastMessageAt (itm@ gItem 'lastMessageAt))
					)
					(switch
						(= lastMessageAt 0)
							Nil

						(= lastMessageAt timeToDieInSeconds)
							Nil

						(= timeToDieInSeconds 0)
							(block (
								(missionID (itm@ gItem 'missionID))
								(missionObj (and missionID (objGetObjByID missionID)))
								)
								(if (= gSource gPlayerShip)
									(objSendMessage gSource Nil (typTranslate gType 'msgPatientHasDied))
									)

								(objSet@ gSource gItem 'lastMessageAt 0)

								(if missionObj
									(msnFireEvent missionObj 'OnRejuvPatientStatusChanged {
										item: gItem
										status: 'dead
										})
									)
								)

						(!= gSource gPlayerShip)
							Nil

						(and (gr lastMessageAt 30) (leq timeToDieInSeconds 30))
							(block (
								(missionID (itm@ gItem 'missionID))
								(missionObj (and missionID (objGetObjByID missionID)))
								)
								(objSendMessage gSource Nil (typTranslate gType 'msgPatientCritical))
								(objSet@ gSource gItem 'lastMessageAt timeToDieInSeconds)

								(if missionObj
									(msnFireEvent missionObj 'OnRejuvPatientStatusChanged {
										item: gItem
										status: 'critical
										})
									)
								)

						(and (leq timeToDieInSeconds 30)
								(= (mod timeToDieInSeconds 5) 0)
								)
							(block ()
								(objSendMessage gSource Nil (typTranslate gType 'msgPatientWillDieIn {
									time: timeToDieInSeconds
									}))
								(objSet@ gSource gItem 'lastMessageAt timeToDieInSeconds)
								)
						)
					)

ItemType.Invoke:
			(block ()
				(sysCreateWeaponFire &itSapiensRadiationMine; 
						{
							obj: gSource
							namePattern: (typ@ &itSapiensRadiationMine; 'namePattern)
							}
						gSource 
						(random 0 359)
						5 
						nil)

				(objRemoveItem gSource gItem 1)
				)

