(block Nil
	; Vaughn's armor is resistant to laser and kinetic (only 20% damage gets through)
	(objEnumItems gSource "aI" theItem
		(shpEnhanceItem gSource theItem {
			enhancement: "+resistDamageTier:laser:20;"
			type: &itCentauriNanos;
			})
		)
	)

