Events.GetParameters:
(block (
	(damageHP (@ gData 'damageHP))
	(weaponUNID (@ gData 'weaponUNID))
	)

	{
		radius: (if (= damageHP 0) 10 (mathScale damageHP 5 64 20 45 50))
		;lifetime: (if (= damageHP 0) 4 (mathScale damageHP 5 64 12 48 50))
	
		intensity: (seededRandom weaponUNID 50 70)
		distortion: (mathScale damageHP 5 64 50 0 100)
		detail: (seededRandom weaponUNID 20 25)
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	(speed (@ gData 'speed))
	)
	{
		length:	(mathScale damageHP	2 30 speed (@ speed 2) 50)
		width: (mathScale damageHP 2 30 12 28 50)
		}
	)

				(block (damageHP)
					(setq damageHP (@ gData 'damageHP))

					{
						length:	(mathScale damageHP	2 30 (divide (@ gData 'speed) 2) (@ gData 'speed) 50)
						width: (mathScale damageHP 2 30 12 36 50)
						}
					)

							(block (
								(damageHP (@ gData 'damageHP))
								)

								{
									particleCount: (mathScale damageHP 10 140 100 300 50)
									length: 72 ;(mathScale damageHP 10 140 60 220 50)
									width: 9
									}
								)
